    private void AceptarBotonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_AceptarBotonActionPerformed
         String usuario = txtFnombre.getText();
      String clave = new String(txtClave.getPassword());
      if(usuario.equals("admin") && clave.equals("1234")){
          JOptionPane.showMessageDialog(this,"Inicio exitoso");
      }else{
          JOptionPane.showMessageDialog(this,"Usuario o clave incorrectos","Error",JOptionPane.ERROR_MESSAGE);
          //Esto es para limpiar la caja de texto
          txtFnombre.setText("");
          txtClave.setText("");
          txtFnombre.requestFocus();
      }
    }//GEN-LAST:event_AceptarBotonActionPerformed

    private void buttonSalirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buttonSalirActionPerformed
        int confirmacion = JOptionPane.showConfirmDialog(this, "estas saliendo","error",JOptionPane.YES_NO_OPTION);
    }//GEN-LAST:event_buttonSalirActionPerformed

  
  
